Bitwise
Note: When compiling with the directive #include &lt;math.h&gt;, you may need to end the command with -lm. An example follows:
gcc [fileName.c] -o [executableName] -lm
#include &lt;stdio.h&gt;
#include &lt;math.h&gt;

int main(){

    unsigned int a = 10;            // 1010
    unsigned int b = 15;            // 1111


    // bitwise and - &
    unsigned int result = a & b; 
    /*
        1010    &lt;- 10
    &   1111    &lt;- 15
    =   1010    -&gt; 10
    */  
    printf("Ex 1: %d\n", result);



    // bitwise or - |
    unsigned int result2 = 10 | 15;
    /*
        1010    &lt;- 10
    |   1111    &lt;- 15
    =   1111    -&gt; 15
    */  
    printf("Ex 2: %d\n", result2);



    // bitwise not/flip/ones complement - ~
    unsigned int result3 = ~a;      
    // ~a -&gt; ~1010 but....
    // remember if an unsigned int is 4 bytes, then we have:
    //      00000000 00000000 00000000 00001010 
    // So bit flipping results in: 
    //      11111111 11111111 11111111 11110101 -&gt; 4,294,967,285
    printf("Ex 3: %u\n", result3);



    // bitwise xor - ^
    unsigned int result4 = a ^ b;
    /*
        1010    &lt;- 10
    ^   1111    &lt;- 15
    =   0101    -&gt;  5
    */  
    printf("Ex 4: %u\n", result4);


    // right shift - &gt;&gt;
    /*
          15 &gt;&gt; 3  
        1111 &gt;&gt; 3 -&gt; 0001 -&gt; 1
    */
    u_int32_t result5 = b &gt;&gt; 3;
    printf("Ex 5: %u\n", result5);


    // left shift - &lt;&lt;
    /*
          15 &lt;&lt; 2  
        1111 &lt;&lt; 2 -&gt; 111100 -&gt; 60
    */
    u_int32_t result6 = b &lt;&lt; 2;
    printf("Ex 5: %u\n", result6);


    // Shifting can be used for power of 2 multiplication and division:

    // Left shift for power of 2 multiplication:
    // It's faster for a machine than traditional multiplication
    // u &lt;&lt; k = u * pow(2, k)
    int u = 3; 
    int k = 2;
    printf("%d\n", u &lt;&lt; k);
    printf("%lf\n", u * pow(2, k));  // requires #include &lt;math.h&gt; directive 

    // Right shift for power of 2 division:
    // u &gt;&gt; k = u / pow(2, k)
    int u2 = 2500;
    int k2 = 3;
    printf("%d\n", u2 &gt;&gt; k2);
    printf("%f", u2 / pow(2, k2));  // requires #include &lt;math.h&gt; directive 

    printf("\n");
    return 0;

}

#include &lt;stdio.h&gt;

// structs are collections of variables of different types
// the members are like properties/state in Object oriented programming
// memory is allocated for each member separately
// all members values can be accessed/used at the same time

struct Person{
    int age;        // location of first member is the same as the location of the struct
    char name[20];  // location of the second member is adjacent to the first
};

int main(){
    struct Person bill = {10, "Bill"};
    struct Person amanda = {20, "Amanda"};

    // look at bill
    printf("%s is %d years old\n", bill.name, bill.age);
    printf("%s is located at %p\n", bill.name, &bill);
    printf("%s's age is located at %p\n", bill.name, &bill.age);
    printf("%s's name is located at %p\n", bill.name, &bill.name);

    // look at amanda
    printf("%s is %d years old\n", amanda.name, amanda.age);
    printf("%s is located at %p\n", amanda.name, &amanda);
    printf("%s's age is located at %p\n", amanda.name, &amanda.age);
    printf("%s's name is located at %p\n", amanda.name, &amanda.name);

    return 0;
}

