void bubbleSort(int *aList, size_t listSize){
    for (int i=0; i &lt; listSize - 1; i++){
        for (int j = 0; j &lt; listSize - 1 - i;  j++){
            if (aList[j+1] &lt; aList[j]){
                // swap
                int temp = aList[j];
                aList[j] = aList[j+1];
                aList[j+1] = temp;
            }
        }
    }
}

